    private void initComponents() {

        messagePanel = new javax.swing.JPanel();
        processingProgressBar = new javax.swing.JProgressBar();
        processingLabel = new javax.swing.JLabel();

        setDefaultCloseOperation(javax.swing.WindowConstants.DO_NOTHING_ON_CLOSE);
        setTitle("Processing");
        setResizable(false);

        messagePanel.setBorder(javax.swing.BorderFactory.createTitledBorder("Wait a moment"));
        messagePanel.setLayout(new java.awt.BorderLayout());

        processingProgressBar.setIndeterminate(true);
        processingProgressBar.setPreferredSize(new java.awt.Dimension(150, 18));
        messagePanel.add(processingProgressBar, java.awt.BorderLayout.SOUTH);

        processingLabel.setText("processing...");
        messagePanel.add(processingLabel, java.awt.BorderLayout.NORTH);

        getContentPane().add(messagePanel, java.awt.BorderLayout.CENTER);

        pack();
    }// </editor-fold>//GEN-END:initComponents

    public static MessageDialog show(JFrame parent, String message) {
        MessageDialog ms = new MessageDialog(parent);
        ms.processingLabel.setText(message);
        ms.pack();
        ms.setSize(500, ms.getSize().height);
        ms.setLocationRelativeTo(parent);
        ms.setVisible(true);

        return ms;
    }

    public void setMessage(String message) {
        processingLabel.setText(message);
    }

    public void close() {
        dispose();
    }
    
